import styles from './input.module.css'

// Exemplo simples de Input
function Input({ text, name, type, value, onChange, onBlur }) {
  return (
    <div>
      <label htmlFor={name}>{text}</label>
      <input
        id={name}
        name={name}
        type={type}
        value={value}
        onChange={onChange}
        onBlur={onBlur} // importantíssimo repassar o onBlur!
      />
    </div>
  );
}


export default Input;
