// ScheduleForm.jsx
import { useState } from 'react';
import axios from 'axios';
import Inputt from '../form/Input'; // seu componente customizado
import SubmitButton from '../form/SubmitButton';

function AgendamentForm() {
  const [form, setForm] = useState({
    nomePet: '',
    nomeTutor: '',
    idadePet: '',
    celular: '',
    email: '',
    cep: '',
    rua: '',
    numero: '',
    cidade: '',
    estado: '',
    motivo: '',
    dataConsulta: '',
    horaConsulta: ''
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/agendamentos', form);
      alert('Consulta agendada com sucesso!');
      // limpar se quiser
    } catch (err) {
      alert('Erro ao agendar: ' + (err.response?.data?.message || 'erro desconhecido'));
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <Inputt type="text" text="Nome do pet" name="nomePet" onChange={handleChange} />
      <Inputt type="text" text="Nome do Tutor" name="nomeTutor" onChange={handleChange} />
      <Inputt type="text" text="Idade do Pet" name="idadePet" onChange={handleChange} />
      <Inputt type="number" text="Número de celular" name="celular" onChange={handleChange} />
      <Inputt type="text" text="Email" name="email" onChange={handleChange} />
      <Inputt type="number" text="CEP" name="cep" onChange={handleChange} />
      <Inputt type="text" text="Rua" name="rua" onChange={handleChange} />
      <Inputt type="number" text="Número" name="numero" onChange={handleChange} />
      <Inputt type="text" text="Cidade" name="cidade" onChange={handleChange} />
      <Inputt type="text" text="Estado" name="estado" onChange={handleChange} />
      <Inputt type="text" text="O que está acontecendo com o pet?" name="motivo" onChange={handleChange} />
      <Inputt type="date" text="Data da Consulta" name="dataConsulta" onChange={handleChange} />
      <Inputt type="time" text="Horario da Consulta" name="horaConsulta" onChange={handleChange} />
      <SubmitButton text="Agendar Consulta" />
    </form>
  );
}

export default AgendamentForm;
