import React, { useState } from 'react';
import styles from './agendamentform.module.css';
import Inputt from '../form/Input';
import SubmitButton from '../form/SubmitButton';

function AgendamentForm() {
  const [formData, setFormData] = useState({
    nomePet: '',
    nomeTutor: '',
    idadePet: '',
    celular: '',
    email: '',
    cep: '',
    rua: '',
    numero: '',
    cidade: '',
    estado: '',
    motivo: '',
    dataConsulta: '',
    horaConsulta: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await fetch('http://localhost:5000/agendamentos', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData)
    });

    const data = await res.json();
    if (res.ok) {
      alert('Consulta agendada com sucesso!');
    } else {
      alert('Erro: ' + data.error);
    }
  };

  return (
    <form className={styles.form} onSubmit={handleSubmit}>
      <Inputt type="text" text="Nome do pet" name="nomePet" value={formData.nomePet} onChange={handleChange} />
      <Inputt type="text" text="Nome do Tutor" name="nomeTutor" value={formData.nomeTutor} onChange={handleChange} />
      <Inputt type="text" text="Idade do Pet" name="idadePet" value={formData.idadePet} onChange={handleoChange} />
      <Inputt type="number" text="Numero de celular" name="celular" value={formData.celular} onChange={handleChange} />
      <Inputt type="text" text="Email" name="email" value={formData.email} onChange={handleChange} />
      <Inputt type="number" text="CEP" name="cep" value={formData.cep} onChange={handleChange} />
      <Inputt type="text" text="Rua" name="rua" value={formData.rua} onChange={handleChange} />
      <Inputt type="number" text="Número" name="numero" value={formData.numero} onChange={handleChange} />
      <Inputt type="text" text="Cidade" name="cidade" value={formData.cidade} onChange={handleChange} />
      <Inputt type="text" text="Estado" name="estado" value={formData.estado} onChange={handleChange} />
      <Inputt type="text" text="O que está acontecendo com o pet?" name="motivo" value={formData.motivo} onChange={handleChange} />
      <Inputt type="date" text="Data da Consulta" name="dataConsulta" value={formData.dataConsulta} onChange={handleChange} />
      <Inputt type="time" text="Horario da Consulta" name="horaConsulta" value={formData.horaConsulta} onChange={handleChange} />
      <SubmitButton text="Agendar Consulta" />
    </form>
  );
}

export default AgendamentForm;
