import { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // <-- importar
import axios from 'axios';
import Input from '../form/Input';
import SubmitButton from '../form/SubmitButton';

function AgendamentForm() {
  const navigate = useNavigate(); // <-- usar aqui

  const [form, setForm] = useState({
    nomePet: '',
    nomeTutor: '',
    idadePet: '',
    celular: '',
    email: '',
    cep: '',
    rua: '',
    numero: '',
    cidade: '',
    estado: '',
    motivo: '',
    dataConsulta: '',
    horaConsulta: ''
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const dados = {
        pet: form.nomePet,
        nome_tutor: form.nomeTutor,
        idade_pet: Number(form.idadePet),
        Telefone: form.celular,
        Email: form.email,
        Rua: form.rua,
        Cidade: form.cidade,
        Estado: form.estado,
        Motivo: form.motivo,
        Data: form.dataConsulta,
        Horario: form.horaConsulta
      };

      await axios.post('http://localhost:5000/agendamento', dados);

      navigate('/', { state: dados }); // <--- redireciona passando os dados
    } catch (err) {
      alert('Erro ao agendar: ' + (err.response?.data?.message || 'erro desconhecido'));
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <Input type="text" text="Nome do pet" name="nomePet" onChange={handleChange} value={form.nomePet} />
      <Input type="text" text="Nome do Tutor" name="nomeTutor" onChange={handleChange} value={form.nomeTutor} />
      <Input type="text" text="Idade do Pet" name="idadePet" onChange={handleChange} value={form.idadePet} />
      <Input type="number" text="Número de celular" name="celular" onChange={handleChange} value={form.celular} />
      <Input type="text" text="Email" name="email" onChange={handleChange} value={form.email} />
      <Input type="text" text="CEP" name="cep" onChange={handleChange} value={form.cep} />
      <Input type="text" text="Rua" name="rua" onChange={handleChange} value={form.rua} />
      <Input type="text" text="Número" name="numero" onChange={handleChange} value={form.numero} />
      <Input type="text" text="Cidade" name="cidade" onChange={handleChange} value={form.cidade} />
      <Input type="text" text="Estado" name="estado" onChange={handleChange} value={form.estado} />
      <Input type="text" text="O que está acontecendo com o pet?" name="motivo" onChange={handleChange} value={form.motivo} />
      <Input type="date" text="Data da Consulta" name="dataConsulta" onChange={handleChange} value={form.dataConsulta} />
      <Input type="time" text="Horario da Consulta" name="horaConsulta" onChange={handleChange} value={form.horaConsulta} />
      <SubmitButton text="Agendar Consulta" />

    </form>
  );
}

export default AgendamentForm;
