import { useState } from 'react';
import axios from 'axios';
import Input from '../form/Input';
import SubmitButton from '../form/SubmitButton';

function AgendamentForm() {
  const [form, setForm] = useState({
    nomePet: '',
    nomeTutor: '',
    idadePet: '',
    celular: '',
    email: '',
    cep: '',
    rua: '',
    numero: '',
    cidade: '',
    estado: '',
    motivo: '',
    dataConsulta: '',
    horaConsulta: ''
  });

  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};

    if (!form.nomePet) newErrors.nomePet = 'Nome do pet é obrigatório';
    if (!form.nomeTutor) newErrors.nomeTutor = 'Nome do tutor é obrigatório';
    if (!form.celular) newErrors.celular = 'Celular é obrigatório';
    if (!form.email) newErrors.email = 'Email é obrigatório';
    if (!form.cep) newErrors.cep = 'CEP é obrigatório';
    if (!form.rua) newErrors.rua = 'Rua é obrigatória';
    if (!form.cidade) newErrors.cidade = 'Cidade é obrigatória';
    if (!form.estado) newErrors.estado = 'Estado é obrigatório';
    if (!form.motivo) newErrors.motivo = 'Motivo é obrigatório';
    if (!form.dataConsulta) newErrors.dataConsulta = 'Data é obrigatória';
    if (!form.horaConsulta) newErrors.horaConsulta = 'Horário é obrigatório';

    return newErrors;
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setErrors(prevErrors => ({
      ...prevErrors,
      [e.target.name]: ''
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateForm();

    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    setLoading(true);
    try {
      const dados = {
        pet: form.nomePet,
        nome_tutor: form.nomeTutor,
        idade_pet: form.idadePet,
        Telefone: form.celular,
        Email: form.email,
        Rua: form.rua,
        Cidade: form.cidade,
        Estado: form.estado,
        Motivo: form.motivo,
        Data: form.dataConsulta,
        Horario: form.horaConsulta
      };

      await axios.post('http://localhost:3000/agendamento', dados);
      alert('Consulta agendada com sucesso!');
      setForm({
        nomePet: '',
        nomeTutor: '',
        idadePet: '',
        celular: '',
        email: '',
        cep: '',
        rua: '',
        numero: '',
        cidade: '',
        estado: '',
        motivo: '',
        dataConsulta: '',
        horaConsulta: ''
      });
      setErrors({});
    } catch (err) {
      alert('Erro ao agendar: ' + (err.response?.data?.message || 'erro desconhecido'));
    } finally {
      setLoading(false);
    }
  };

  const formatPhone = (value) => {
    if (!value) return '';
    const phone = value.replace(/\D/g, '');
    if (phone.length <= 11) {
      return phone.replace(/(\d{2})(\d)/, '($1) $2').replace(/(\d{5})(\d)/, '$1-$2');
    }
    return value;
  };

  const formatCep = (value) => {
    if (!value) return '';
    const cep = value.replace(/\D/g, '');
    if (cep.length <= 8) {
      return cep.replace(/(\d{5})(\d)/, '$1-$2');
    }
    return value;
  };

  return (
    <form onSubmit={handleSubmit} className="agendament-form">
      <Input 
        type="text" 
        text="Nome do pet" 
        name="nomePet" 
        onChange={handleChange} 
        value={form.nomePet}
        error={errors.nomePet}
        required
      />
      <Input 
        type="text" 
        text="Nome do Tutor" 
        name="nomeTutor" 
        onChange={handleChange} 
        value={form.nomeTutor}
        error={errors.nomeTutor}
        required
      />
      <Input 
        type="text" 
        text="Idade do Pet" 
        name="idadePet" 
        onChange={handleChange} 
        value={form.idadePet}
        error={errors.idadePet}
      />
      <Input 
        type="tel" 
        text="Número de celular" 
        name="celular" 
        onChange={(e) => handleChange({ ...e, target: { ...e.target, value: formatPhone(e.target.value) } })}
        value={form.celular}
        error={errors.celular}
        required
      />
      <Input 
        type="email" 
        text="Email" 
        name="email" 
        onChange={handleChange} 
        value={form.email}
        error={errors.email}
        required
      />
      <Input 
        type="text" 
        text="CEP" 
        name="cep" 
        onChange={(e) => handleChange({ ...e, target: { ...e.target, value: formatCep(e.target.value) } })}
        value={form.cep}
        error={errors.cep}
        required
      />
      <Input 
        type="text" 
        text="Rua" 
        name="rua" 
        onChange={handleChange} 
        value={form.rua}
        error={errors.rua}
        required
      />
      <Input 
        type="number" 
        text="Número" 
        name="numero" 
        onChange={handleChange} 
        value={form.numero}
        error={errors.numero}
      />
      <Input 
        type="text" 
        text="Cidade" 
        name="cidade" 
        onChange={handleChange} 
        value={form.cidade}
        error={errors.cidade}
        required
      />
      <Input 
        type="text" 
        text="Estado" 
        name="estado" 
        onChange={handleChange} 
        value={form.estado}
        error={errors.estado}
        required
      />
      <Input 
        type="text" 
        text="O que está acontecendo com o pet?" 
        name="motivo" 
        onChange={handleChange} 
        value={form.motivo}
        error={errors.motivo}
        required
      />
      <Input 
        type="date" 
        text="Data da Consulta" 
        name="dataConsulta" 
        onChange={handleChange} 
        value={form.dataConsulta}
        error={errors.dataConsulta}
        required
      />
      <Input 
        type="time" 
        text="Horário da Consulta" 
        name="horaConsulta" 
        onChange={handleChange} 
        value={form.horaConsulta}
        error={errors.horaConsulta}
        required
      />
      <SubmitButton text="Agendar Consulta" disabled={loading} />
    </form>
  );
}

export default AgendamentForm;
