import React, { useState, useEffect } from 'react';
import styles from './consultasagendadas.module.css';

function ConsultasAgendadas() {
    const [appointments, setAppointments] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchAppointments();
    }, []);

    const fetchAppointments = async () => {
        try {
            const response = await fetch('http://localhost:3000/agendamentos');
            const data = await response.json();
            setAppointments(data);
            setLoading(false);
        } catch (error) {
            console.error('Erro ao buscar consultas:', error);
            setLoading(false);
        }
    };

    if (loading) {
        return <div className={styles.loading}>Carregando consultas...</div>;
    }

    return (
        <div className={styles.container}>
            <h2>Consultas Agendadas</h2>
            {appointments.length === 0 ? (
                <p>Nenhuma consulta agendada.</p>
            ) : (
                <div className={styles.appointmentsList}>
                    {appointments.map(appointment => (
                        <div key={appointment.id} className={styles.appointmentCard}>
                            <h3>{appointment.name}</h3>
                            <p><strong>Tutor:</strong> {appointment.tutor}</p>
                            <p><strong>Data:</strong> {appointment.date}</p>
                            <p><strong>Hor√°rio:</strong> {appointment.time}</p>
                            <p><strong>Motivo:</strong> {appointment.reason}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default ConsultasAgendadas;
