import {Route, BrowserRouter as Router, Routes, Link, BrowserRouter, Navigate, useLocation} from 'react-router-dom'
import Agendamento from './components/pages/agendconsul'
import Consultas from './components/pages/consultas'
import Container from './components/layout/Container';
import footer from './components/layout/Footer';
import Navbar from './components/layout/Navbar';
import Footer from './components/layout/Footer';
import DetalhesConsulta from './components/agendamentos/DetalhesConsulta';
import EditarConsulta from './components/agendamentos/EditarConsulta';
import Login from './components/pages/login'




function App() {

  const isAuth = localStorage.getItem('autenticado') === 'true';
  const location = useLocation();

  // Verifica se é a página de login
  const isLoginPage = location.pathname === '/';


  return (


    <BrowserRouter>
    {!isLoginPage && <Navbar />}



      <Routes>
            <Route path="/" element={<Login />} />
            <Route path="/agendamento" element={isAuth ? <Agendamento/> : <Navigate to="/" />} />
            <Route path="/consultas" element={isAuth ? <Consultas/> : <Navigate to="/" />} />
            <Route path="/detalhes-consulta" element={isAuth ? <DetalhesConsulta /> : <Navigate to="/"/>} />
            <Route path="/editar-consulta" element={isAuth ? <EditarConsulta /> : <Navigate to="/"/>} />

    </Routes> 
   <Footer/>


  </BrowserRouter>



  );
}

export default App;
