import styles from './agendamentform.module.css'
import Inputt from '../form/Input'
import SubmitButton from '../form/SubmitButton'

function AgendamentForm(){

    const handleSubmit = async e => {
    e.preventDefault();
    const res = await fetch('http://localhost:3000/agendamentos', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
    });
    const data = await res.json();
    if (res.ok) {
        alert('Consulta agendada com sucesso!');
    } else {
        alert('Erro: ' + data.error);
    }
};


    return(
        <form className={styles.form}>

            <Inputt
                type="text"
                text="Nome do pet"
                name="name"
                placeholder="Nome do pet"
                value={formData.name}
                onChange={handleChange}
            />
            <Inputt
                type="text"
                text="Nome do Tutor"
                name="tutor"
                placeholder="Nome do/a Pai ou Mãe do pet"
                value={formData.tutor}
                onChange={handleChange}
            />
            <Inputt
                type="text"
                text="Idade do Pet"
                name="idade"
                placeholder="Idade do Pet"
                value={formData.idade}
                onChange={handleChange}
            />
            <Inputt
                type="number"
                text="Numero de celular"
                name="name"
                placeholder="Numero de celular"
            />
            <Inputt
                type="text"
                text="Email"
                name="email"
                placeholder="E-mail"
                value={formData.email}
                onChange={handleChange}
            />

            <Inputt 
                type="number"
                text="CEP" 
                name="cep"
                placeholder="CEP"
                value={formData.cep}
                onChange={handleChange}
            />


            <Inputt 
                type="text" 
                text="Rua"
                name="street"
                placeholder="Rua"
                value={formData.street}
                onChange={handleChange}
            />


            <Inputt 
                type="number" 
                text="Número"
                name="number"
                placeholder="Numero"
                value={formData.number}
                onChange={handleChange}
            />


            <Inputt 
                type="text" 
                text="Cidade"
                name="city"
                placeholder="Cidade"
                value={formData.city}
                onChange={handleChange}
            />


            <Inputt 
                type="text" 
                text="Estado"
                name="state"
                placeholder="Estado"
                value={formData.state}
                onChange={handleChange}
            />


            <Inputt 
                type="text"
                text="O que está acontecendo com o pet?"
                name="reason"
                placeholder="Motivo da consulta"
                value={formData.reason}
                onChange={handleChange}
            />

            <Inputt 
                type="date" 
                text="Data da Consulta"
                name="date"
                value={formData.date}
                onChange={handleChange}
            />

            <Inputt 
                type="time" 
                text="Horario da Consulta"
                name="time"
                value={formData.time}
                onChange={handleChange}
            />

           <SubmitButton text="Agendar Consulta"/>

        </form>
    )   
}

export default AgendamentForm
