import React, { useState } from 'react';
import styles from './agendamentform.module.css';
import Inputt from '../form/Input';
import SubmitButton from '../form/SubmitButton';

function AgendamentForm() {
    const [formData, setFormData] = useState({
        name: '',
        tutor: '',
        idade: '',
        phone: '',
        email: '',
        cep: '',
        street: '',
        number: '',
        city: '',
        state: '',
        reason: '',
        date: '',
        time: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await fetch('http://localhost:3000/agendamentos', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData)
            });
            const data = await res.json();
            if (res.ok) {
                alert('Consulta agendada com sucesso!');
                setFormData({
                    name: '',
                    tutor: '',
                    idade: '',
                    phone: '',
                    email: '',
                    cep: '',
                    street: '',
                    number: '',
                    city: '',
                    state: '',
                    reason: '',
                    date: '',
                    time: ''
                });
            } else {
                alert('Erro: ' + data.error);
            }
        } catch (error) {
            alert('Erro ao agendar consulta: ' + error.message);
        }
    };

    return (
        <form className={styles.form} onSubmit={handleSubmit}>
            <Inputt
                type="text"
                text="Nome do pet"
                name="name"
                placeholder="Nome do pet"
                value={formData.name}
                onChange={handleChange}
            />
            {/* ... restante dos campos do formul√°rio ... */}
            <SubmitButton text="Agendar Consulta" />
        </form>
    );
}

export default AgendamentForm;
